class Cookie
  attr_accessor :name,:price,:description,:picture
  def initialize(name,price,description,picture)
      @name = name
      @price = price
      @description = description
      @picture = picture
  end
end

class Cakes
  attr_accessor :name,:price,:description,:picture
  def initialize(name,price,description,picture)
      @name = name
      @price = price
      @description = description
      @picture = picture
  end
end

class Muffin
  attr_accessor :name,:price,:description,:picture
  def initialize(name,price,description,picture)
      @name = name
      @price = price
      @description = description
      @picture = picture
  end
end

